angular.module("psicoterApp",[]).controller("patientController",function(e,t){e.patient={},e.addPatient=function(){var a=$.param({data:e.patient});t.post("api/action.php",a,{headers:{"Content-Type":"application/x-www-form-urlencoded;charset=utf-8;"}}).then(function(t){"OK"==t.data.status?(e.patientForm.$setPristine(),e.patient={},e.messageSuccess(t.data.msg)):(e.patientForm.$setPristine(),e.patient={},e.messageError(t.data.msg))})},e.messageSuccess=function(e){$(".alert-success > p").html(e),$(".alert-success").show(),$(".alert-success").delay(15e3).slideUp(function(){$(".alert-success > p").html(""),$(".alert-success").addClass("none")})},e.messageError=function(e){$(".alert-danger > p").html(e),$(".alert-danger").show(),$(".alert-danger").delay(15e3).slideUp(function(){$(".alert-danger > p").html(""),$(".alert-success").addClass("none")})}});